sources.src\\sass\\_burgerHeading.scss:
$bodyBackground: #fff2e5;
$rollBackground: #f3a156;
$rollReflectBackground: #f6b880;
$rollSesameBackground: #cb7527;
$tomatoBackground: #e43d3d;
$lettuceBackground: #5db255;
$cheeseBackground: #f6cf54;
$chopBackground: #a84545;
$chopTraceBackground: #8f3b3b;

.burger {
  width: 252px;
  height: 178px;
  margin: auto;
  transform: scale(0.8);
  &:after {
    content: "";
    display: block;
    width: calc(100% + 20px);
    height: 5px;
    background: #a9a9a9;
    opacity: 0.4;
    margin-top: 1px;
    border-radius: 100%;
    margin-left: -10px;
    position: relative;
    z-index: -2;
  }
}
.roll {
  &--top {
    height: 85px;
    overflow: hidden;
    border-bottom-left-radius: 7px;
    border-bottom-right-radius: 7px;
    z-index: 3;
    transform: translateY(-125px) rotate(-20deg);
    animation: rotateRoll 1s linear 0.27s forwards;

    &:after {
      content: "";
      display: block;
      width: 285px;
      height: 350px;
      border-radius: 80% 80% 50% 50% / 60% 60% 40% 40%;
      position: absolute;
      background: #f3a156;
      left: -17px;
      right: 0;
      top: 6px;
      margin: auto;
    }
    position: relative;
    &:before {
      content: "";
      display: block;
      width: 281px;
      height: 350px;
      border-radius: 80% 80% 50% 50% / 62% 62% 40% 40%;
      position: absolute;
      background: #f6b880;
      left: -15px;
      top: 0;
      z-index: 0;
    }
  }
  &--bottom {
    width: 252px;
    height: 27px;
    border-radius: 30px 30px 60px 60px;
    background: $rollBackground;
    position: relative;
    &:after {
      content: "";
      display: block;
      width: 100%;
      height: 27px;
      margin-top: 3px;
      position: absolute;
      background: #d79d68;
      border-radius: inherit;
      z-index: -1;
    }
  }
  &__sesame {
    background: $rollSesameBackground;
    height: 6px;
    width: 6px;
    display: block;
    position: absolute;
    border-radius: 100%;
    z-index: 3;
    &:nth-child(1) {
      left: 45px;
      top: 40px;
    }
    &:nth-child(2) {
      left: 95px;
      top: 20px;
    }
    &:nth-child(3) {
      left: 140px;
      top: 20px;
    }
    &:nth-child(4) {
      left: 170px;
      top: 63px;
    }
    &:nth-child(5) {
      left: 190px;
      top: 35px;
    }

    &:nth-child(6) {
      left: 30px;
      top: 65px;
    }
    &:nth-child(7) {
      left: 75px;
      top: 50px;
    }
    &:nth-child(8) {
      left: 132px;
      top: 50px;
    }
    &:nth-child(9) {
      left: 210px;
      top: 70px;
    }
    &:nth-child(10) {
      left: 100px;
      top: 67px;
    }
  }
}
@keyframes rotateRoll {
  30% {
    transform: translateY(-40px) rotate(-20deg);
  }
  50% {
    transform: translateY(0px) rotate(0deg);
  }
  75% {
    transform: translateY(-4px) rotate(2deg);
  }
  100% {
    transform: translateY(0px) rotate(0deg);
  }
}

.lettuce {
  transform: translateY(-75px) rotate(-15deg);
  width: 252px;
  height: 8px;
  border-radius: 20px;
  background: $lettuceBackground;
  display: flex;
  flex-direction: column;
  position: relative;
  z-index: 2;
  animation: rotateLettuce 0.5s linear forwards;

  &__top,
  &__bottom {
    display: flex;
  }
  &__top {
    margin-top: -4px;
  }
  &__bottom {
    margin-top: -5px;
  }
  &__el {
    width: 14px;
    height: 10px;
    border-radius: 100%;
    background: $lettuceBackground;
  }
}

@keyframes rotateLettuce {
  35% {
    transform: translateY(-55px) rotate(-10deg);
  }
  100% {
    transform: translateY(0px) rotate(0deg);
  }
}

.cheese {
  transform: translateY(-45px) rotate(-10deg);
  width: 252px;
  height: 8px;
  border-radius: 20px;
  background: $cheeseBackground;
  position: relative;
  margin-bottom: -6px;
  animation: rotateCheese 0.5s linear forwards;
  z-index: 1;
  &__el {
    background: inherit;
    position: absolute;

    &--right {
      right: 57px;
      animation-delay: 0.9s;
    }
    &--left {
      animation-delay: 0.7s;
      left: 25px;
    }
    &--big {
      width: 42px;
      height: 42px;
      border-radius: 4px;
      right: 20px;
      top: -25px;
      transform: translateY(-42px) rotate(45deg);
      opacity: 0;
      animation: meltCheese 0.9s 1.1s forwards;
    }
    &--small {
      width: 16px;
      height: 16px;
      border-radius: 1.5px;
      top: -1px;
      transform: translateY(-16px) rotate(45deg);
      opacity: 0;
      animation-name: meltCheese;
      animation-duration: 0.55s;
      animation-fill-mode: forwards;
    }
  }
}

@keyframes meltCheese {
  25% {
    opacity: 1;
  }
  100% {
    transform: translateY(0) rotate(45deg);
    opacity: 1;
  }
}

@keyframes rotateCheese {
  35% {
    transform: translateY(-35px) rotate(-7deg);
  }
  100% {
    transform: translateY(0px) rotate(0deg);
  }
}

.chop {
  transform: translateY(-30px) rotate(-5deg);
  position: relative;
  width: 252px;
  height: 38px;
  border-radius: 20px;
  background: $chopBackground;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  z-index: 0;
  overflow: hidden;
  animation: rotateChop 0.5s linear forwards;
  &__top,
  &__bottom {
    display: flex;
    justify-content: space-around;
  }
  &__trace {
    border-radius: 5px 5px 0 0;
    width: 5px;
    height: 6px;
    background: $chopTraceBackground;
  }
}

@keyframes rotateChop {
  35% {
    transform: translateY(-10px) rotate(-3deg);
  }
  100% {
    transform: translateY(0px) rotate(0deg);
  }
}

.tomato-section {
  display: flex;
  justify-content: space-between;
  position: relative;
  z-index: 3;
  &__el {
    width: 80px;
    height: 12px;
    display: block;
    background: $tomatoBackground;
    border-radius: 20px;

    &:nth-child(1) {
      transform: translateY(-85px) rotate(-25deg);
      animation: rotateTomato 0.55s linear forwards;
    }
    &:nth-child(2) {
      transform: translateY(-105px) rotate(-25deg);
      animation: rotateTomato 0.55s linear 0.12s forwards;
    }
    &:nth-child(3) {
      transform: translateY(-125px) rotate(-25deg);
      animation: rotateTomato 0.55s linear 0.24s forwards;
    }
  }
}

@keyframes rotateTomato {
  90% {
    transform: translateY(-5px) rotate(-10deg);
  }
  100% {
    transform: translateY(0px) rotate(0deg);
  }
}

sources.src\\sass\\_bookmark.scss:
.bookmarkListBox {
  display: none;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: start;
  width: 90%;
  margin: 50px auto;
  .bookmarkResult {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr;
    gap: 0px 0px;
    grid-template-areas:
      "bookmarkImg bookmarkTitle bookmarkTitle bookmarkTitle"
      "bookmarkImg bookmarkAuthor bookmarkAuthor bookmarkAuthor";
    align-items: center;
    height: 120px;
    width: 280px;
    margin: 10px;
    border-radius: 10px;
    border: 1px solid rgb(230, 230, 230);
    box-shadow: 0px 4px 24px -1px $boxShadowColor;
    cursor: pointer;
    transition: 0.3s ease-in;

    &:hover {
      transform: translateY(3px) scale(1.03);
    }

    .bookmarkImage {
      grid-area: bookmarkImg;
      align-self: center;
      justify-self: center;
      width: 75px;
      height: 75px;
      border-radius: 50%;
      margin: 15px;
    }
    .bookmarkTitle {
      grid-area: bookmarkTitle;
      display: inline-block;
      justify-self: start;
      align-self: end;
      width: 130px;
      height: 28px;
      white-space: nowrap;
      font-size: 17px;
      text-overflow: ellipsis;
      overflow: hidden;
      font-family: $regular-font;
      margin-left: 10px;
      color: $purpleDark;
    }
    .bookmarkAuthor {
      grid-area: bookmarkAuthor;
      justify-self: start;
      align-self: start;
      font-size: 14px;
      font-family: $regular-font;
      margin-left: 10px;
      margin-top: 5px;
    }
  }
}
.bookmarkVisible {
  display: flex;
}

